<?php

namespace jubianchi\BehatViewerBundle\Entity\Repository;

use \Doctrine\ORM\EntityRepository,
    \Doctrine\ORM\Query\Expr,
    \jubianchi\BehatViewerBundle\Entity;

/**
 * FeatureRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class FeatureRepository extends EntityRepository
{
    public function findBySlugAndBuild($slug, Entity\Build $build)
    {
        return $this->createQueryBuilder('f')
            ->select(array('f'))
            ->where('f.slug = :slug')
            ->andWhere('f.build = :build')
            ->setParameter('slug', $slug)
            ->setParameter('build', $build)
            ->getQuery()
            ->getOneOrNullResult();
    }

    public function findByTagAndBuild(Entity\Tag $tag, Entity\Build $build)
    {
        return $this->createQueryBuilder('f')
            ->select(array('f', 'ft'))
            ->join('f.tags', 'ft', Expr\Join::WITH, 'ft = :tag')
            ->where('f.build = :build')
            ->setParameter('tag', $tag)
            ->setParameter('build', $build)
            ->getQuery()
            ->getResult();
    }
}